import React, { PureComponent } from "react";
import {
  TouchableOpacity,
  View,
  Image,
  Text,
  Dimensions,
  StyleSheet
} from "react-native";
//import { COLORS, SHADOW } from "../../Common/const/Colors";
//import { showMessage } from "../../../Route/BottomNavigator";
//const { master, slave, white, red } = COLORS;
const { width, height } = Dimensions.get("screen");

let boxWidth = width * 0.41;

const styles = StyleSheet.create({
  itemWrapper: {
    flexDirection: "row",
    width: boxWidth,
    marginLeft: "6%",
    justifyContent: "center"
  },
  itemHolder: {
    marginBottom: 15,
    width: boxWidth - 5,
    borderTopEndRadius: 100,
    borderTopStartRadius: 100,
    borderBottomLeftRadius: 33,
    borderBottomRightRadius: 33,
    backgroundColor: "#fff",
    paddingBottom: 15,
    alignItems: "center"
  },
  itemImage: {
    width: boxWidth,
    height: boxWidth,
    justifyContent: "center",
    borderRadius: boxWidth / 2,
    borderColor: "#fff",
    borderWidth: 2,
    backgroundColor: "#fff"
  },
  itemTime: {
    textAlign: "center",
    fontSize: 13,
    position: "absolute",
    color: "#fff",
    top: boxWidth - 30
  },
  itemText: {
    textAlign: "center",
    fontSize: 14,
    marginTop: 15,
    color: "#fff"
  },
  gradient: {
    width: boxWidth - 4,
    height: boxWidth - 2,
    borderRadius: boxWidth / 2,
    position: "absolute"
  }
});



class VideoListItem extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {};
  }

  componentDidMount() {}
/*
  message() {
    showMessage({
      text: "you are not vip!",
      time: 5,
      onTimeEnd: showMessage({ show: false }),
      buttonOnPress: undefined,
      buttonTitle: "VIP",
      buttonType: "Icon",
      icon: "vip",
      show: true
    });
  }
  */

  render() {
    const {
      itemHolder,
      itemImage,
      gradient,
      itemTime,
      itemText,
      itemWrapper
    } = styles;
    return (
      <TouchableOpacity
        style={itemWrapper}
        onPress={
          !(this.props.user && this.props.user.is_vip) && this.props.is_vip
            ? () => this.message()
            : () => this.props.onItemClick(this.props)
        }
      >
        <View style={[itemHolder]}>
          {this.props.is_vip ? (
            <View
              style={{
                backgroundColor: "#FF0000",
                width: boxWidth * 0.15 * 2,
                height: boxWidth * 0.15,
                position: "absolute",
                left: 5,
                borderRadius: (boxWidth * 0.15) / 2,
                transform: [{ rotate: "-50deg" }],
                top: boxWidth - boxWidth * 0.17,
                justifyContent: "center"
              }}
            >
              <Text
                style={{
                  transform: [{ rotate: "50deg" }],
                  color: "#fff",
                  fontSize: 9,
                  top: (boxWidth * 0.15) / 1.8,
                  left: 0,
                  fontWeight: "bold"
                }}
              >
                {"VIP"}
              </Text>
            </View>
          ) : null}

          <View
            style={{
              alignItems: "center"
            }}
          >
            <Image
              style={itemImage}
              square
              source={{ uri: this.props.thumbnail }}
            />
            <Image
              style={gradient}
              source={require("./gradientBackground.png")}
            />
          </View>
          <Text style={itemTime}>{this.props.duration}</Text>

          <Text style={itemText}>{this.props.title}</Text>
        </View>
      </TouchableOpacity>
    );
  }
}

export default VideoListItem;
