import React, { Component } from 'react';
import {
  View,
  Text,
  SafeAreaView,
  StatusBar,
  Image,
  TextInput,
  Button,
  TouchableOpacity,
  StyleSheet,
  FlatList
} from 'react-native';
import PaintAnimalScreen from "../../screen/main/new_painting/paint/PaintAnimalScreen";
import NavigationActions from "../../router/NavigationActions";


const styles = StyleSheet.create({
  container: {
    flex: 1,
    //flexDirection: 'row',
    //marginTop: 120
  },
  backImage: {
    width: '100%', 
    height: '100%'
  },
  logo: {
    position: 'absolute',
    top: 250,
    bottom: 0,
    left: 200,
    right: 0
  }
});

class MainScreen extends Component {
  render() {
    let quizImage = require('../../../../assets/quiz.jpg')
    let storyImage = require('../../../../assets/poster.jpg')
    let singImage = require('../../../../assets/sing.jpg')

    const { navigation } = this.props;
    //console.log(navigation)

    return (
      <View style={styles.container}>
        <Image source={require('../../../../assets/App_Background.png')} style={styles.backImage}/>
        <View style={styles.logo}>
          <TouchableOpacity
          onPress={() =>
            NavigationActions.navigate('QuizIndex')
          }
          activeOpacity={0.8}
          style={{
            backgroundColor: '#ffffff',
            width: 160,
            margin: 10,
            height: 180,
            borderRadius: 10,
            padding: 15,
            shadowColor: '#9e9898',
            elevation: 5,
          }}>
          <Image
            source={quizImage}
            style={{
              width: '100%',
              resizeMode: 'cover',
              flex: 1,
            }}
          />
          <Text style={{marginTop: 20, textAlign: 'center', fontSize: 16}}>
          Movie Quizz
          </Text>
        </TouchableOpacity>
              <TouchableOpacity
            onPress={() =>
                NavigationActions.navigate("PaintAnimals")
              //navigation.navigate('PaintAnimalScreen')
            }
            activeOpacity={0.8}
            style={{
              backgroundColor: '#ffffff',
              width: 160,
              margin: 10,
              height: 180,
              borderRadius: 10,
              padding: 15,
              shadowColor: '#9e9898',
              elevation: 5,
            }}>
            <Image
              source={storyImage}
              style={{
                width: '100%',
                resizeMode: 'cover',
                flex: 1,
              }}
            />
            <Text style={{marginTop: 20, textAlign: 'center', fontSize: 16}}>
            Coloring Time
            </Text>
          </TouchableOpacity>
          </View>
          </View>
    

    );
  }
}

export default MainScreen;
