import React, { Component } from "react";
import { StyleSheet, FlatList, Dimensions, ScrollView } from "react-native";
//import { connect } from "react-redux";
import VideoListItem from "./VideoListItem";
import { ifIphoneX } from "react-native-iphone-x-helper";
const { height } = Dimensions.get("screen");

const style = StyleSheet.create({
    list: {
      height: "100%"
    },
    listWrapper: { height: height, marginTop: ifIphoneX ? 130 : 115 }
  });
  

class VideoListsScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      page: 1,
      dataSource: [
        {
          id: "1v-",
          title: "I Painted On Paint Palettes...",
          description:
            "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
          video: "http://clips.vorwaerts-gmbh.de/big_buck_bunny.mp4",
          thumbnail: "https://picsum.photos/301",
          image: "https://picsum.photos/401",
          duration: "01:43",
          is_vip: 0
        },
        {
          id: "2v-",
          title: "Full Bushcraft    Shelter Build ...",
          description:
            "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
          video:
            "https://file-examples.com/wp-content/uploads/2017/04/file_example_MP4_480_1_5MG.mp4",
          thumbnail: "https://picsum.photos/300",
          image: "https://picsum.photos/400",
          duration: "05:54",
          is_vip: 1
        },
        {
          id: "3v-",
          title: "I Made That Awful Soap Hand",
          description:
            "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
          video:
            "https://file-examples.com/wp-content/uploads/2017/04/file_example_MP4_480_1_5MG.mp4",
          thumbnail: "https://picsum.photos/310",
          image: "https://picsum.photos/410",
          duration: "07:05",
          is_vip: 0
        },
        {
          id: "4v-",
          title: "Let's Paint                   With String",
          description:
            "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
          video:
            "https://file-examples.com/wp-content/uploads/2017/04/file_example_MP4_480_1_5MG.mp4",
          thumbnail: "https://picsum.photos/330",
          image: "https://picsum.photos/430",
          duration: "11:14",
          is_vip: 0
        },
        {
          id: "5v-",
          title: "Google Deep Dive With Me",
          description:
            "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
          video:
            "https://file-examples.com/wp-content/uploads/2017/04/file_example_MP4_480_1_5MG.mp4",
          thumbnail: "https://picsum.photos/500",
          image: "https://picsum.photos/500",
          duration: "03:44",
          is_vip: 0
        },
        {
          id: "6v-",
          title: "Reviewing Bad Apps",
          description:
            "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
          video:
            "https://file-examples.com/wp-content/uploads/2017/04/file_example_MP4_480_1_5MG.mp4",
          thumbnail: "https://picsum.photos/120",
          image: "https://picsum.photos/240",
          duration: "07:22",
          is_vip: 0
        }
      ],
      isFinished: false, //checks is loading list item finished or not.
      isLoading: false
    };

    this.onItemClick = this.onItemClick.bind(this);
  }

  onItemClick(item) {
    //const { navigation } = this.props;
    //console.log(this.props.navigation)
    this.props.navigation.push("VideoScreen", {...item});
  }

  render() {
    return (
      <ScrollView style={style.listWrapper}>
        <FlatList
          style={style.list}
          data={this.state.dataSource}
          keyExtractor={item => {
            return item.id;
          }}
          onEndReachedThreshold={0.5}
          renderItem={({ item, index }) => {
            return (
              <VideoListItem
                {...item}
                key={item.id}
                onItemClick={item => {
                  this.onItemClick(item);
                }}
              />
            );
          }}
          numColumns={2}
        />
      </ScrollView>
    );
  }
}


export default VideoListsScreen;
