import React, {useState, Component} from 'react';
import {View, Text, StyleSheet, Image, TouchableOpacity, Button, Box, ScrollView, StatusBar, Dimensions } from 'react-native';
//import { SafeAreaView } from 'react-native-safe-area-context';
import QuestionList from '../../component/QuestionList';
import {Buttons, ButtonContainer} from '../../component/Buttons';
import {Alert} from '../../component/Alert';
const { width, height } = Dimensions.get("window");


const styles = StyleSheet.create({
    container: {
        //backgroundColor: "#fff",
        flex: 1,
        //paddingHorizontal: 20,
        //width: width,
        //height: height/2,
        //borderRadius:50,
    },
    safearea: {
        flex: 1,
        marginTop: 100,
        justifyContent: "space-between"
    },
    text: {
        color: "#000000",
        fontSize: 26,
        textAlign: "center",
        //letterSpacing: -0.02,
        //fontWeight: "600"
    },
    text2: {
        color: "#000000",
        fontSize: 32,
        textAlign: "center",
        marginTop: 60,
        marginLeft: -30
    },
    container2: {
        height: height/3, 
        backgroundColor: "#F5CDDE", 
        borderBottomRightRadius: 150,
        borderBottomLeftRadius: 150
    }
    
});

class QuizDetailScreen extends React.Component {
    state = {
        correctCount: 0,
        //totalCount: this.props.navigation.getParam("questions", []).length,
        totalCount: QuestionList.length,
        activeQuestionIndex: 0,
        answered: false,
        answerCorrect: false

    }

    answer = correct => {
        this.setState(
            state => {
                const nextState={ answered: true};

                if (correct) {
                    nextState.correctCount = state.correctCount + 1;
                    nextState.answerCorrect = true;
                } else {
                    nextState.answerCorrect = false;
                }

                return nextState;
            },
            () => {
                setTimeout(() => this.nextQuestion(), 750);
            }
        )
    }

    nextQuestion = () => {
        this.setState(state => {
            const nextIndex = state.activeQuestionIndex + 1;

            if(nextIndex >= state.totalCount) {
                return this.props.navigation.popToTop();
            }

            return {
                activeQuestionIndex: nextIndex,
                answered: false
            }
        })
    }
    render() {
        const question = QuestionList[this.state.activeQuestionIndex];

        return (
            <View style={styles.container}>
            <View style={styles.container2}>
                <SafeAreaView style={styles.safearea}>
                <Text style={styles.text}>{question.question}</Text>
                </SafeAreaView> 
                </View>
                <ButtonContainer>
                    {question.answerList.map(answer => (
                        <Buttons
                        key={answer.id}
                        text={answer.text}
                        onPress={() => this.answer(answer.correct)}
                        />
                    ))}
                </ButtonContainer>
                <Text style={styles.text2}>
                    {`${this.state.correctCount}/${this.state.totalCount}`}
                </Text>
                <Alert
                correct={this.state.answerCorrect}
                visible={this.state.answered}
                /> 
            </View> 
            
        )};
}

export default QuizDetailScreen;
