import * as React from 'react';
import{
    Component
} from 'react'
import * as d3 from 'd3'
import {View, StyleSheet} from 'react-native';
//import AsyncStorage from '@react-native-async-storage/async-storage';

import { Audio } from "expo-av";
import Svg, {
    Path,
    G,
    ClipPath,
    Defs,
    Use
  } from 'react-native-svg';

import {inject, observer} from "mobx-react";
import { SOUND } from '../../Constant';

@inject('paintStore')
@observer
export default class ShapeKai3 extends Component {
  constructor(props){
    super(props);
    this.audio = null;
  }

  componentDidMount() {
    //this._playSound();
  }

  componentWillUnmount() {
  }






    render() {
    const {data, numberScale, numberPath, id, idPath} = this.props;
    const {paint} = this.props;
    const dataShape = paint == "Animals" ? this.props.paintStore.pointAnimals[id].point[idPath]:this.props.paintStore.pointObjects[id].point[idPath];

    //console.log("render path2 " + data.number);
    //const dataPath = dataShape.path.length == 0 ? "" : lineFunction(dataShape.path);
   // console.log(dataShape.listData)
    const dataPath='';
    console.log(dataShape)


   console.log('a')
   console.log(this.props.paintStore)
    return (

      <G>

      <Defs>  

          <Path  id={"path" + data.number}
              d = {data.d}
              stroke={data.stroke} 
              strokeWidth="0.8"
              opacity={data.opacity}         
              />


              <Use id={"shape" + data.number} href={"#path" + data.number} />
              <ClipPath id={"clip" + data.number}>
                <Use transform={{  scale: numberScale}}  id={"shape" + data.number} href={"#path" + data.number} x = {this.props.paintStore.posX} y = {this.props.paintStore.posY}/>
            </ClipPath>         
        </Defs>

       <Use transform={{scale: numberScale}}   href={"#shape" + data.number}  fill={data.fill} />



       <G clipPath={"url(#clip" + data.number + ")"}>
        {
          dataShape.listData.map((item, index) =>
          <Path 
            transform={{ scale: numberPath}}
            key={index}
            d={item.data}
            fill="none"
            stroke={item.color}
            strokeWidth={item.strokeWidth}
            strokeLinejoin="round"
            strokeLinecap="round"

          />)
        }
              <Path
              d={dataPath}
              fill="none"
              stroke={this.props.paintStore.color}
              strokeWidth={this.props.paintStore.strokeColor}
              strokeLinejoin="round"
              strokeLinecap="round"
            />



      </G>
      </G>




    );

  }
}
