import React, { Component } from "react";
import {
  Text,
  Dimensions,
  View,
  Image,
  ScrollView,
  StyleSheet,
  Animated,
  TouchableOpacity,
  ActivityIndicator,
  Platform
} from "react-native";
//import { connect } from "react-redux";
//import Video from "react-native-video";
import { Video } from 'expo-av';
import BgSvg from "./BgSvg";
import SvgUri from "react-native-svg-uri";
import Play from "./common/Play.svg.js";
import { ifIphoneX } from "react-native-iphone-x-helper";
import { SHADOW } from "./common/Colors";



const { width, height } = Dimensions.get("screen");
const swidth = width * 0.8;

const styles = StyleSheet.create({
    itemWrapper: {
        width: swidth,
        alignSelf: "center",
        display: "flex",
        flexDirection: "column",
        backgroundColor: "white",
        marginBottom: 15,
        borderBottomLeftRadius: 23,
        borderBottomRightRadius: 23
      },
      itemVideo: {
        //height: swidth,
        //height: 700,
        //width: 650,
        //backgroundColor: "black",
        //alignSelf: "center"
       // resizeMode: "center"
      },
      itemTitle: {
        textAlign: "center",
        fontSize: 14,
        padding: 10,
        fontWeight: "bold"
      },
      itemContent: {
        textAlign: "center",
        fontSize: 13,
        padding: 10,
        paddingTop: 0
      },
      videoWrapper: {
        overflow: "hidden",
        borderColor: "#fff",
        borderWidth: 2,
        width: swidth - 2,
        height: swidth - 2,
        alignSelf: "center"
      },
      loadingWrapper: {
        height: swidth,
        width: swidth,
        borderRadius: swidth / 2,
        position: "absolute",
        justifyContent: "center",
        overflow: "hidden"
      },
      loading: { alignSelf: "center" },
      playWrapper: {
        height: swidth,
        width: swidth,
        borderRadius: swidth / 2,
        position: "absolute",
        justifyContent: "center",
        overflow: "hidden"
      },
      gradient: {
        width: swidth - 6,
        height: swidth - 6,
        borderRadius: swidth / 2,
        position: "absolute",
        alignSelf: "center"
      },
      play: {
        height: "100%",
        width: "100%",
        alignSelf: "center"
      },
      contentWrapper: {
        padding: 10,
        borderRadius: 10,
        width: "100%",
        alignSelf: "center"
      }
 
});

class VideoScreen extends Component {
    player;

    constructor(props) {
      super(props);
      this.state = {
        mustShowAds: true,
        videoSource: "",
        isLoading: false,
        isPaused: false,
        showPlay: false,
        radiusAnimation: new Animated.Value(swidth / 2)
      };
  
      this.onEndVideo = this.onEndVideo.bind(this);
      this.goBack = this.goBack.bind(this);
      this.onBuffer = this.onBuffer.bind(this);
      this.startAnimation = this.startAnimation.bind(this);
      this.stopAnimation = this.stopAnimation.bind(this);
      this.playAgain = this.playAgain.bind(this);
    }
  
    onEndVideo() {
      this.stopAnimation();
      if (this.state.mustShowAds) {
        this.setState({ mustShowAds: false });
      }
    }
  
    componentWillMount() {
      //there is no ads to show
      //hideMenu();
      // showMenu();
    }
  
    componentWillUnmount() {
      //showMenu();
    }
  
    goBack() {
      this.props.navigation.goBack();
    }
  
    onBuffer() {
      this.setState({
        isLoading: true
      });
    }
  
    startAnimation = () => {
      this.setState({
        isLoading: false,
        isPaused: false
      });
      Animated.spring(this.state.radiusAnimation, {
        toValue: 0,
        duration: 700
      }).start();
    };
  
    stopAnimation = () => {
      this.setState({
        showPlay: true
      });
      Animated.spring(this.state.radiusAnimation, {
        toValue: swidth / 2,
        duration: 700
      }).start();
    };
  
    playAgain = () => {
      this.startAnimation();
      this.player.seek(0);
      this.setState({
        isPaused: false,
        showPlay: false
      });
    };

      render() {
        const {
          itemWrapper,
          itemVideo,
          itemTitle,
          itemContent,
          videoWrapper,
          loading,
          loadingWrapper,
          playWrapper,
          gradient,
          play,
          contentWrapper
        } = styles;
    
        return (
          <ScrollView
            style={{
              marginTop: ifIphoneX ? 130 : 115,
              backgroundColor: Platform.OS === "ios" ? background : ""
            }}
          >
            {Platform.OS === "ios" ? (
              <View>
                <SvgUri
                  style={{ position: "absolute", top: -100 }}
                  width={width}
                  height={height / 1.9}
                  svgXmlData={BgSvg}
                />
              </View>
            ) : (
              undefined
            )}
            <Animated.View
              style={[
                itemWrapper,
                SHADOW,
                {
                  borderTopLeftRadius: this.state.radiusAnimation,
                  borderTopRightRadius: this.state.radiusAnimation
                }
              ]}
            >
              <Animated.View
                style={[videoWrapper, { borderRadius: this.state.radiusAnimation }]}
              >
                <Video
                  onEnd={this.onEndVideo}
                  resizeMode="cover"
	                style={{ width, height: 300 }}
                  //style={itemVideo}
                  shouldPlay
                  //ref={ref => {
                  //  this.player = ref;
                  //}}
    
                  // poster={this.props.navigation.getParam("image")}
                  source={{ uri: this.props.navigation.getParam("video") }}
                  navigation={this.props.navigation}
                  controls={true}
                  paused={this.state.isPaused}
                  onLoadStart={this.onBuffer}
                  onLoad={this.startAnimation}
                />
              </Animated.View>
    
              {this.state.isLoading ? (
                <View style={loadingWrapper}>
                  <Image
                    style={gradient}
                    source={require("./gradientBackground.png")}
                  />
                  <ActivityIndicator color="#ffffff" style={loading} />
                </View>
              ) : null}
    
              {this.state.showPlay ? (
                <TouchableOpacity
                  onPress={() => {
                    this.playAgain();
                  }}
                  style={playWrapper}
                >
                  <Image
                    style={gradient}
                    source={require("./gradientBackground.png")}
                  />
                  <Play
                    style={play}
                    height="120"
                    width="120"
                    isPlaying={false}
                    color="#fff"
                    //color={master}
                  />
                </TouchableOpacity>
              ) : null}
    
              <View style={contentWrapper}>
                <Text style={itemTitle}>
                  {this.props.navigation.getParam("title", "")}
                </Text>
                <Text style={itemContent}>
                  {this.props.navigation.getParam("description", "")}
                </Text>
              </View>
            </Animated.View>
          </ScrollView>
)}
}


export default VideoScreen;
