export const COLORS = {
    master: "#FDBB3F",
    slave: "#343434",
    white: "#FFFFFF",
    background: "#F7F7F7",
    red: "#FD3F3F",
    green: "#44FD3F",
    error: "#FF0000"
  };
  export const SHADOW = {
    shadowColor: "rgba(0, 0, 0, 0.6)",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.23,
    shadowRadius: 2.62,
    elevation: 8
  };
  
  /*
    wrapper size
    width:90%
    marginTop:35
    
  */
  export const opacity = (color, opacity) => color + opacity;
