import {sizeFont, sizeWidth} from "../../src/util/Size";

export const APP_COLOR = '#000';
export const TUTORIAL_DOT_COLOR_INACTIVE = '#00C8BA';
export const TUTORIAL_DOT_COLOR_ACTIVE = 'radial-gradient(4.00px at 50% 50%, #656AFF 0%, rgba(101, 106, 255, 0.69) 100%)';
export const STATUS_BAR_COLOR = '#FFFFFF';
export const TOOLBAR_COLOR = '#FFFFFF';
export const COLOR_APP_BLACK = '#000000';
export const COLOR_APP_WHITE = '#FFFFFF';
export const COLOR_APP_BLUE = 'radial-gradient(28.00px at 50% 50%, #656AFF 0%, rgba(101, 106, 255, 0.69) 100%)';
export const COLOR_APP_BUTTON = '#8871FF';
export const COLOR_APP_GRAY = '#7B7B7B';
export const COLOR_APP_RED = 'radial-gradient(28.00px at 50% 50%, #FF3F3F 0%, rgba(255, 101, 101, 0.69) 100%)';
export const LOADING_COLOR = '#FFF';
export const APP_COLOR_YELLOW = '#FFD600';
export const APP_BACKGROUND = '#E9E9E9';
export const SEPARATOR_COLOR = '#CACACA';
export const APP_TEXT_COLOR = '#FFF';
export const APP_TEXT_BLACK = '#222222';
export const APP_TEXT_APP = '#595959';
export const APP_TEXT_APP_2 = '#959595';
export const APP_TEXT_PLACEHOLDER = '#8c8c8c';
export const COLOR_APP_SAVE = 'radial-gradient(20.00px at 50% 50%, #0075FF 0%, rgba(0, 117, 255, 0.69) 100%)';
export const COLOR_APP_CLEAR = 'radial-gradient(20.00px at 50% 50%, #FF7A00 0%, rgba(255, 152, 56, 0.69) 100%)';


export const FONT_FAMILY_APP = 'expo-font'
export const TOOL_BAR_TEXT = {
    fontSize: sizeFont(4.53),
    // fontWeight:'bold',
    color: 'white'
};
export const BOX_SHADOW = {
    shadowOffset: {width: 0, height: sizeWidth(0.1)},
    shadowOpacity: 0.1,
    marginVertical: sizeWidth(2),
    backgroundColor: COLOR_APP_WHITE,
    marginHorizontal: sizeWidth(2),
    shadowRadius: sizeWidth(2),
    elevation: 8,
}
export const COLOR_PLACEHOLDER = "#C1C1C1";
