renderItemPen = ({ item , index}) => {
  numberSelectPen = this.props.paintStore.getNumberSelectPen();
  //console.log(source)
  source = numberSelectPen == 1 ? item.img_1
            : numberSelectPen == 2 ? item.img_2
            : numberSelectPen == 3 ? item.img_3
            : numberSelectPen == 4 ? item.img_4
            : null;
  if (numberSelectPen == 4)
  {
    stylesImg = styles.ImgColorPen4;
  }
  else
  {
    stylesImg = styles.ImgColor;
  }

  return (
    <Color source={source} id={item.id} isPen={false} onPress={this._onPressItem(item)} styleTouch={styles.TouchColor} styleImg={stylesImg}  key={index} />
  );

};

_onPressItem(item){
  return(event) =>{
      //this.setState({colorDraw: item.color});
      //console.log(item.color)
      this.props.paintStore.setColor(item.color);
      this.props.paintStore.setColorPen(item.id);
  }
}


}



const styles = StyleSheet.create({
    container: {
        flex: 1,
      },
      backgroundImage: {
        resizeMode: "stretch",
        position: "absolute",
        width: "100%",
        height: "100%"
      },
      button_save: {
        width: sizeWidth(25),
        marginTop: -150,
        marginLeft: 280
        //backgroundColor: COLOR_APP_SAVE,
      },
      contentImage: {
        marginTop: sizeHeight(4),
        alignItems: "center",
        justifyContent: "center",
        height: sizeHeight(40.5),
        width: sizeWidth(90.5),
        backgroundColor: "#fff", 
        borderRadius: sizeWidth(6),
        flexDirection: "row",
      },
      contentImageKai: {
        position: "absolute",
        alignItems: "center",
        justifyContent: "center",
        height: sizeHeight(40.5),
        width: sizeWidth(60.5),
        borderRadius: sizeWidth(6),
        flexDirection: "row",
      },
      contentColor: {
        flex: 1,
        marginTop: sizeHeight(4),
        height: sizeHeight(35.5),
        width: "100%",
        backgroundColor: "#fff", 
        borderTopLeftRadius: sizeWidth(6),
        alignItems: "center",
        justifyContent: "center",
      },
      contentPenColor: {
        flex: 1,
        paddingHorizontal: sizeWidth(5),
        height: sizeHeight(10.5),
        width: "100%",
        backgroundColor: "#fff",
        borderTopLeftRadius: sizeWidth(6), 
        alignItems: "center",
        justifyContent: "center",
      },
      ImgColor: {
        resizeMode: "center",
        width: sizeWidth(5.5),
      },
      ImgColorPen4 : {
        resizeMode: "center",
        width: sizeWidth(8),
        height: sizeHeight(8.5),
      },
      TouchColor: {
        paddingTop: sizeHeight(10),
        width: sizeWidth(8.5),
        height: sizeHeight(8.5),
      },
      contentPen: {
        flex: 2,
        paddingHorizontal: sizeWidth(5),
        height: sizeHeight(25),
        width: "100%",
        backgroundColor: "#8B8FFF", 
        borderTopLeftRadius: sizeWidth(6),
        alignItems: "center",
        justifyContent: "center",
      },
      selectPen: {
        flex: 1,
        marginHorizontal: sizeWidth(3),
        marginVertical: sizeHeight(4),
        borderRadius: sizeWidth(20),
        backgroundColor: "#CED0FF",
        alignItems: "center",
        justifyContent: "center",
      },
      ImgPen: {
        resizeMode: "center",
        width: sizeWidth(5),
      },
      ImgPen4 : {
        resizeMode: "center",
        width: sizeWidth(8),
      },
      contentHeader: {
        height: sizeHeight(8),
        width: sizeWidth(90.5),
        marginTop: 40,
        marginLeft: 20,
        alignItems: "center",
        justifyContent: "space-between",
        flexDirection: 'row',
      }
});
