import React, { Component } from "react";
import {
  FlatList,
  Image,
  StyleSheet,
  TouchableOpacity,
  View,
  Dimensions 
} from "react-native";
import _ from "lodash";
import ToolBar from "../../../../component/ToolBar";
import AppText from "../../../../component/Text";
import styleApp from "../../../../../res/style/style";
import { strings } from "../../../../config/i18n/i18n";
import { ANIMALS_DATA } from "../../../../../Constant";
import { sizeFont, sizeWidth, sizeHeight } from "../../../../util/Size";
import NavigationActions from "../../../../router/NavigationActions";

const win = Dimensions.get('window');


export default class LearnAlphabetScreen extends Component {

  state = {
    studiedAlphabet: [],
    studiedNumber: []
  };


  render() {


    return (
      <View style={styles.container}>
        <Image
          style={styles.backgroundImage}
          source={require("../../../../../../assets/backg.jpg")}
        />   
        {this.renderToolbar()}
        {this.renderAnimals()}
      </View>
    );
  }

  renderToolbar = () => {
    return (
      <ToolBar
        center={
          <AppText style={styleApp.ToolBarText}>

          </AppText>
        }
      />
    );
  };

  renderAnimals = () => {
    return (
      <FlatList

        data={ANIMALS_DATA}
        style={{
          margin: sizeWidth(5),
        }}

        //showsVerticalScrollIndicator={false}
        numColumns={1}
        keyExtractor={(item, index) => index.toString()}
        renderItem={this.renderItemAnimal}
      />
    );
  };

  renderItemAnimal = ({ item }) => {
    return (
      <TouchableOpacity
        onPress={() => this.onPressAnimal({ item })}
        style={[
          styles.view,


        ] }
      > 
        <Image source={item.url} 
          style={{ width: '100%', height: 170, margin: 20,  }}/>    
      </TouchableOpacity>
    );
  };

//style={{width: sizeWidth(90.4), borderRadius: 100,}}

  onPressAnimal = ({ item }) => {
    const { navigation } = this.props;
    const studiedAlphabets = navigation.getParam("studiedAlphabet");
    const studiedNumber = navigation.getParam("studiedNumber");

    NavigationActions.navigate("LetPaint", {
      paint: item.paint,
      item: item,
      currentIndex: ANIMALS_DATA.findIndex(letter => letter.id == item.id),
      needIndexFromNavigation: true,
      isNumber: false,
      studiedAlphabet: studiedAlphabets,
      studiedNumber: studiedNumber
    });
  };
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  backgroundImage: {
    resizeMode: "stretch",
    position: "absolute",
    width: "100%",
    height: "100%"
  },
  newPaintImage: {
    position: "absolute",
    left: "-8%",
    width: "128%",
    height: "100%"
  },
  view: {
    flex: 1,
    //justifyContent: "space-between",
    //alignItems: "center",
    //justifyContent: "center",
    //width: sizeWidth(6.4),
    //height: sizeHeight(17.4),
   // marginHorizontal: sizeWidth(8),
    //marginVertical: sizeWidth(6),
    borderRadius: sizeWidth(6)
  }
});
